class Faonni_CallBack_Block_Adminhtml_Request_Grid_Renderer_Profile
	extends Mage_Adminhtml_Block_Widget_Grid_Column_Renderer_Abstract
{
    /**
     * Renders grid column
     *
     * @param   Varien_Object $row
     * @return  string
     */
    public function render(Varien_Object $row)
    {
		return $row->getCustomerId() 
			? '<a href="' . $this->getUrl('*/customer/edit', array('id' => $row->getCustomerId())) . '">' . Mage::helper('faonni_callback')->__('Customer Information') . '</a>'
			: '';
    }
}